// src/pages/DebatePage.js

import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useDebate } from '../contexts/DebateContext';
import DebateTheater from '../components/debate/DebateTheater';
import DebateTimeline from '../components/debate/DebateTimeline';
import EvidencePanel from '../components/debate/EvidencePanel';
import InteractionPanel from '../components/debate/InteractionPanel';
import ArgumentVisualization from '../components/visualization/ArgumentVisualization';
import LoadingIndicator from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import '../assets/styles/DebatePage.css';

const DebatePage = () => {
  const { topicId } = useParams();
  const navigate = useNavigate();
  const [isInitialized, setIsInitialized] = useState(false);
  
  const { 
    topic, 
    selectedExperts,
    loading,
    error, 
    transcript,
    argumentMap,
    activeCitation,
    debateControls,
    startDebate,
    resetDebate,
    updateDebateControls
  } = useDebate();

  useEffect(() => {
    // If there's no topic or experts selected, redirect to home
    if (!topic && !isInitialized) {
      const savedTopic = sessionStorage.getItem('selectedTopic');
      const savedExperts = sessionStorage.getItem('selectedExperts');
      
      if (savedTopic && savedExperts) {
        // Initialize debate with saved values
        const parsedExperts = JSON.parse(savedExperts);
        startDebate(savedTopic, parsedExperts).then(() => {
          setIsInitialized(true);
        });
      } else {
        // Redirect to home if no saved state
        navigate('/');
      }
    }
  }, [topic, isInitialized, navigate, startDebate]);

  // Handle errors
  useEffect(() => {
    if (error) {
      console.error('Debate error:', error);
      // You could show an error message to the user here
    }
  }, [error]);

  const handleBackToHome = () => {
    resetDebate();
    navigate('/');
  };

  if (loading && !isInitialized) {
    return (
      <div className="debate-page loading">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <h2>Initializing Debate...</h2>
          <p>The experts are preparing their perspectives on {topic || 'this topic'}.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="debate-page">
      <header className="debate-header">
        <h1 className="debate-topic">{topic}</h1>
        <div className="debate-controls">
          <button onClick={handleBackToHome} className="back-button">
            Back to Home
          </button>
        </div>
      </header>
      
      <div className="debate-main">
        <div className="debate-theater">
          <DebateTheater 
            transcript={transcript}
            argumentMap={argumentMap}
          />
          
          <DebateTimeline />
        </div>
        
        <div className="sidebar">
          <EvidencePanel 
            activeCitation={activeCitation}
          />
          
          <InteractionPanel 
            debateControls={debateControls}
            onControlsChange={updateDebateControls}
          />
        </div>
      </div>
    </div>
  );
};

export default DebatePage;
